import React, { useEffect, useState } from "react";
import SockJS from "sockjs-client";

const ChatGame = () => {
    const [player, setPlayer] = useState({ name: 'Alice', x: 0, y: 0 });
    const [socket, setSocket] = useState("null");

    const inputPlayerName = () => {
        const playerName = window.prompt("What's your name?");
        const newPlayer = { ...player, name: playerName };
        setPlayer(newPlayer);
    }

    useEffect(()=>{
        const socket = new SockJS('/game');
        socket.onopen = () => {
            console.log('GameServer Connecting');   
            socket.send(JSON.stringify(newPlayer));
        }
    },[])

    return (
        <div id="board">
            <h1>game</h1>
        </div>
    );
}

export default ChatGame;
