import React, { useEffect, useState } from "react";
import SockJS from "sockjs-client";
import { debounce } from "lodash";

const DirectionControl = () => {
  const [socket, setSocket] = useState(null);
  const [name, setName] = useState(null);
  const [id , setId] = useState(null);

  useEffect(() => {
    const socket = new SockJS("/game");
    socket.onopen = () => {
      setSocket(socket);
      console.log("WebSocket Connection Established");
      const name = window.prompt("What's your name?");
      setName(name);
      console.log(name);
    };

    socket.onmessage = (event) => {
      console.log(event);
      if(event.type === 'message'){
        console.log(event.data)
        setId(event.data)
        console.log(id)
      }
    }

    socket.onclose = () => {
      console.log("WebSocket Connection Closed");
    };

    // Clean up the socket on unmount
    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, []);

  useEffect(() => {
    const handleKeyDown = debounce((e) => {
      if (!socket || !id) return;
      switch (e.key) {
        case "ArrowRight":
          const rightSignal = { type: "MOVE", direction: "right" , id:id };
          socket.send(JSON.stringify(rightSignal));
          break;
  
        case "ArrowLeft":
          const leftSignal = { type: "MOVE", direction: "left" , id:id };
          socket.send(JSON.stringify(leftSignal));
          break;
  
        case "ArrowUp":
          const upSignal = { type: "MOVE", direction: "up" , id:id };
          socket.send(JSON.stringify(upSignal));
          break;
  
        case "ArrowDown":
          const downSignal = { type: "MOVE", direction: "down" , id:id };
          socket.send(JSON.stringify(downSignal));
          break;
  
        default:
          break;
      }
    }, 100);

    document.addEventListener("keydown", handleKeyDown);

    // Clean up the event listener on unmount
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, [socket,id]);

  return <div>Direction Control Component</div>;
};

export default React.memo(DirectionControl);
