import React, { useEffect, useState } from "react";
import SockJS from "sockjs-client";
import { debounce } from "lodash";

const DirectionControl = () => {
  const [socket, setSocket] = useState(null);
  const [name, setName] = useState(null);
  const [positions, setPositions] = useState({});

  useEffect(() => {
    const socket = new SockJS("/game");
    socket.onopen = () => {
      setSocket(socket);
      console.log("WebSocket Connection Established");
      const name = window.prompt("What's your name?");
      setName(name);
      const enroll = {"type": "ENROLL" , "name" : name}
      socket.send(JSON.stringify(enroll))
    };

    socket.onmessage = (event) => {
      console.log(event.data);
      try {
        const eventData = JSON.parse(event.data);
        console.log(eventData);
        if(eventData.broadcasting === 'position'){
          const positionData = { ...positions, ...eventData };
          delete positionData["broadcasting"];
          setPositions(positionData);
        }
      } catch (error) {
        console.error("Error parsing JSON data:", event.data);
      }
      
    };

    socket.onclose = () => {
      console.log("WebSocket Connection Closed");
    };

    // Clean up the socket on unmount
    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, []);

  useEffect(() => {
    const handleKeyDown = debounce((e) => {
      if (!socket ) return;
      switch (e.key) {
        case "ArrowRight":
          const rightSignal = { type: "MOVE", direction: "right"};
          socket.send(JSON.stringify(rightSignal));
          break;

        case "ArrowLeft":
          const leftSignal = { type: "MOVE", direction: "left"};
          socket.send(JSON.stringify(leftSignal));
          break;

        case "ArrowUp":
          const upSignal = { type: "MOVE", direction: "up"};
          socket.send(JSON.stringify(upSignal));
          break;

        case "ArrowDown":
          const downSignal = { type: "MOVE", direction: "down"};
          socket.send(JSON.stringify(downSignal));
          break;

        default:
          break;
      }
    }, 100);

    document.addEventListener("keydown", handleKeyDown);

    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, [socket]);

  const getDivStyle = (x, y) => ({
    width: "20px", // 각 칸의 너비 설정
    height: "20px", // 각 칸의 높이 설정
    backgroundColor: "blue", // 배경색 지정
    position: "absolute", // 절대 위치로 설정
    left: `${x * 20}px`, // x 좌표값에 20을 곱하여 위치 지정
    top: `${y * 20}px`, // y 좌표값에 20을 곱하여 위치 지정
    transition: "left 0.2s, top 0.2s", // 이동시 부드럽게 하기 위한 transition 속성
  });

  return (
    <div style={{ position: "relative" }}>
      <h1>Direction Control Component</h1>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(32, 20px)", // 32개의 열, 각 열의 너비는 20px
          gridTemplateRows: "repeat(18, 20px)", // 18개의 행, 각 행의 높이는 20px
        }}
      >
        {/* positionData의 객체들을 이용하여 박스와 이름을 표현 */}
        {Object.entries(positions).map(([name, { x, y }]) => (
          <div key={name} style={getDivStyle(x, y)}>
            <div style={{ color: "white", textAlign: "center", paddingTop: "5px" }}>{name}</div>
          </div>
        ))}
      </div>
    </div>
  );

  
};

export default React.memo(DirectionControl);
