import React, { useEffect, useState } from "react";
import SockJS from "sockjs-client";
import { debounce } from "lodash";


const ChatGame = () => {
    const [playerName, setPlayerName] = useState("");
    const [socket , setSocket] = useState(null);
    const [players , setPlayers] = useState([]);

    const getRandomColor = () => {
        const letters = "0123456789ABCDEF";
        let color = "#";
        for (let i = 0; i < 6; i++) {
          color += letters[Math.floor(Math.random() * 16)];
        }
        return color;
      };
    

    useEffect(() => {
      const playerName = window.prompt("What's your name?");
      setPlayerName(playerName)
    
      const socket = new SockJS('/game');
      socket.onopen = () => {
          const newPlayers = [...players, {name : playerName , x:0 , y:0 , color : `${getRandomColor()}`}]
          setPlayers(newPlayers);

          console.log(newPlayers)
          
          setSocket(socket);
          console.log('GameServer Connecting');
      };
      socket.onmessage = (event) => {
          // const message = JSON.parse(event.data);
          console.log(event)
      };
    }, []);

    //스크립트 코드 쓰기.
    useEffect(() => {
        if (socket) {
          const handleKeyDown = debounce((e) => {
            switch (e.key) {
              case 'ArrowRight':
                const rightSignal = { type: 'MOVE', direction: 'right', name: playerName };
                socket.send(JSON.stringify(rightSignal));
                break;
      
              case 'ArrowLeft':
                const leftSignal = { type: 'MOVE', direction: 'left', name: playerName };
                socket.send(JSON.stringify(leftSignal));
                
      
              case 'ArrowUp':
                const upSignal = { type: 'MOVE', direction: 'up', name: playerName };
                socket.send(JSON.stringify(upSignal));
                break;
      
              case 'ArrowDown':
                const downSignal = { type: 'MOVE', direction: 'down', name: playerName };
                socket.send(JSON.stringify(downSignal));
                break;
            }
          }, 100);
      
          document.addEventListener('keydown', handleKeyDown);
      
          return () => {
            document.removeEventListener('keydown', handleKeyDown);
          };
        }
      }, []);

    return (
        <div id="board">
            <h1>{playerName}</h1>
            {players.map((playerInfo) => (
                <div
                key={playerInfo.name}
                style={{
                    width: "100px",
                    height: "100px",
                    backgroundColor: "black", // Generate a random color for each box
                    position: "absolute",
                    transition: "left 0.3s, top 0.3s",
                    left: `${600 - playerInfo.x * 50}px`,
                    top: `${600 - playerInfo.y * 50}px`,
                }}
                >
                {playerInfo.name}
                </div>
            ))}
        </div>
    );
};

export default ChatGame;
