import React, { useCallback, useEffect, useRef, useState } from "react";
import { createGlobalStyle } from "styled-components";
import reset from "styled-reset";
import SockJS from 'sockjs-client';
import { Stomp } from 'stompjs';

const GlobalStyle = createGlobalStyle`${reset}`;

const ChatApp2 = () => {
    const [msg, setMsg] = useState("");
    const [name, setName] = useState("");
    const [chatt, setChatt] = useState([]);
    const [chkLog, setChkLog] = useState(false);
    const socketRef = useRef(null);

    const msgBox = chatt.map((item, idx) => (
        <div key={idx} className={item.name === name ? "me" : "other"}>
            <span>
                <b>{item.name}</b>
            </span>{" "}
            [{item.data}]
            <br />
            <span>{item.msg}</span>
        </div>
    ));

    useEffect(() => {
        if (!socketRef.current) {
            const socket = new SockJS('http://localhost:8080/wschat');
            const stompClient = Stomp.over(socket);
            socketRef.current = stompClient;

            stompClient.connect({}, function (frame) {
                stompClient.subscribe('/roomname/my-room', function (message) {
                    const receivedMessage = JSON.parse(message.body);
                    setChatt((prevChatt) => [...prevChatt, receivedMessage]);
                });
            });
        }
    }, []);

    const send = useCallback(() => {
        if (!chkLog) {
            if (name === "") {
                alert("이름을 입력하세요.");
                document.getElementById("name").focus();
                return;
            }
            setChkLog(true);
        }
        if (msg !== "") {
            const data = {
                name,
                msg,
                data: new Date().toLocaleDateString(),
            };
            socketRef.current.send("/message/my-room", {}, JSON.stringify(data));
        } else {
            alert("메세지를 입력하세요.");
            document.getElementById("msg").focus();
        }
        setMsg("");
    }, [msg, name, chkLog]);

    return (
        <>
            <GlobalStyle />
            <div id="chat-wrap">
                <div id="chatt">
                    <h1 id="title">WebSocket Chatting</h1>
                    <br />
                    <div id="talk">
                        <div className="talk-shadow"></div>
                        {msgBox}
                    </div>
                    <input
                        disabled={chkLog}
                        placeholder="이름을 입력하세요."
                        type="text"
                        id="name"
                        value={name}
                        onChange={(event) => setName(event.target.value)}
                    />
                    <div id="sendZone">
                        <textarea
                            id="msg"
                            value={msg}
                            onChange={(event) => setMsg(event.target.value)}
                            onKeyDown={(ev) => {
                                if (ev.keyCode === 13) {
                                    send();
                                }
                            }}
                        ></textarea>
                        <input
                            type="button"
                            value="전송"
                            id="btnSend"
                            onClick={send}
                        />
                    </div>
                </div>
            </div>
        </>
    );
};

export default ChatApp2;
