import React, { useEffect, useState } from "react";
import SockJS from "sockjs-client";

const ChatGame = () => {
    const [player, setPlayer] = useState({ name: 'Alice', x: 0, y: 0 });
    const [socket, setSocket] = useState("null");

    useEffect(()=>{
        const socket = new SockJS('/game');
        socket.onopen = () => {
            console.log('GameServer Connecting')
            player.name = window.prompt("What's your name?")
            
            setPlayer(player)
            console.log(player)
            setSocket(socket)
            socket.send(JSON.stringify(player))
        }
    },[])

    return (
        <div id="board">
            <h1>game</h1>
        </div>
    );
}

export default ChatGame;
