import React, { useCallback, useEffect, useRef, useState } from "react";
import { createGlobalStyle } from "styled-components";
import reset from "styled-reset";


const ChatApp = () =>{
    const [msg , setMsg ] = useState("");
    const [name , setName] = useState("");
    const [chatt , setChatt ] = useState([]);
    const [chkLog , setChkLog ] = useState(false);
    const [socketData , setSocketData] = useState();

    const ws = useRef(null);

    const msgBox = chatt.map((item, idx) => (
        <div key={idx} className={item.name === name ? 'me' : 'other'}>
            <span><b>{item.name}</b></span> [{item.data}]<br/>
            <span>{item.msg}</span>
        </div>
    ));

    useEffect(()=>{
        if(socketData !== undefined){
            const tempData = chatt.concat(socketData);
            console.log(tempData);
            setChatt(tempData);
        }
    },[socketData]);

    const GlobalStyle = createGlobalStyle`${reset}`;
    const onText = (event) => {
        console.log(event.target.value)
        setMsg(event.target.value)
    }

    const WebSockectLogin = useCallback(()=> {
        if(!chkLog){
            if(name===""){
                alert("이름을 입력하세요.");
                document.getElementById("name").focus();
                return;
            }
            WebSockectLogin();
            setChkLog(true);
        }
        if(msg !== ''){
            const data = {
                name,
                msg,
                data: new Date().toLocaleDateString(),
            };
            const temp = JSON.stringify(data);
            if(ws.current.readyState === 0){
                ws.current.onopen = () => {
                    console.log(ws.current.readyState);
                    ws.current.send(temp);
                }
            }
            else{
                ws.current.send(temp);
            }
        }else{
            alert("메세지를 입력하세요.")
            document.getElementById("msg").focus();
        }
        setMsg("");
    })
    return (
        <>
            <GlobalStyle/>
            <div id="chat-wrap">
                <div id='chatt'>
                    <h1 id="title">WebSocket Chatting</h1>
                    <br/>
                    <div id='talk'>
                        <div className='talk-shadow'></div>
                        {msgBox}
                    </div>
                    <input disabled={chkLog}
                        placeholder='이름을 입력하세요.' 
                        type='text' 
                        id='name' 
                        value={name} 
                        onChange={(event => setName(event.target.value))}/>
                    <div id='sendZone'>
                        <textarea id='msg' value={msg} onChange={onText}
                            onKeyDown={(ev) => {if(ev.keyCode === 13){send();}}}></textarea>
                        <input type='button' value='전송' id='btnSend' onClick={send}/>
                    </div>
                </div>
            </div>
        </>

    );
}

export default ChatApp;
